console.log(multiply(3, 4));

const nums = [1, 2, 3];
nums.push(4);
console.log(nums.length);

const doubled = nums.map(n => n * 2);
console.log(doubled);

let age = 20;
if (age >= 18) {
  console.log('Adult');
} else {
  console.log('Minor');
}

function makeCounter() {
  let count = 0;
  return () => ++count;
}
const c = makeCounter(); c(); c(); // 1, 2

async function getData() {
  const res = await fetch('/api');
  return res.json();
}

