const wm = new WeakMap();
const obj = {};
wm.set(obj, 'metadata');
wm.get(obj); // 'metadata'

const p = new Proxy({}, {
  get(t, key) { return t[key] ?? 0; }
});
p.x; // 0

-- Insert a new user
INSERT INTO users (name, email, age)
VALUES ('Carol', 'carol@ex.com', 28);

-- Update a record
UPDATE users SET email = 'carol@new.com' WHERE id = 3;

-- Delete a record
DELETE FROM users WHERE id = 5;

CREATE TABLE users (
  id INT PRIMARY KEY AUTO_INCREMENT,
  name VARCHAR(100) NOT NULL
);

BEGIN;
UPDATE accounts SET balance = balance - 100 WHERE id = 1;
UPDATE accounts SET balance = balance + 100 WHERE id = 2;
COMMIT;

SELECT name FROM users
UNION
SELECT name FROM admins;

SELECT name, score,
  ROW_NUMBER() OVER (ORDER BY score DESC) as rn
FROM results;

WITH top_users AS (
  SELECT * FROM users ORDER BY score DESC LIMIT 10
)
SELECT * FROM top_users WHERE active = 1;

CREATE TRIGGER after_insert
AFTER INSERT ON orders
FOR EACH ROW
BEGIN
  UPDATE stats SET total_orders = total_orders + 1;
END;

/* Element selector */
p { color: #333; }

/* Class selector */
.card { background: white; border-radius: 8px; }

/* ID selector */
#hero { font-size: 48px; }

/* Descendant selector */
.nav a { text-decoration: none; }

.container {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    gap: 16px;
}

.item {
    flex: 1; /* grow equally */
}

/* Base styles (mobile) */
.nav { flex-direction: column; }

/* Tablet and above */
@media (min-width: 768px) {
    .nav { flex-direction: row; }
    .sidebar { display: block; }
}

/* Desktop */
@media (min-width: 1024px) {
    .container { max-width: 1200px; }
}

.box {
  width: 200px;
  padding: 20px;
  border: 2px solid #333;
  box-sizing: border-box;
}

font-family: 'Open Sans', sans-serif;
font-size: 16px;
text-align: center;

a:hover { color: blue; }
li:nth-child(odd) { background: #f5f5f5; }
.block::before { content: '»'; }

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}
.box { animation: fadeIn 1s ease; }

button:focus-visible { outline: 2px solid blue; }
@media (prefers-reduced-motion: reduce) {
  * { animation: none; }
}
